// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

// Category.deployed().then(function(i) {cat = i;})
// Factory.deployed().then(function(i) {fact = i;})

contract Category
{
    string public topicHeader;
    uint public opinionCount;

    struct Opinion
    {
        uint id;
        string topic;
        uint voteFor;
        uint voteAgainst;
    }

    mapping (uint => Opinion) public opinions;
    function setTopicHeader(string memory _topicHeader) public
    {
        topicHeader = _topicHeader;
    }

    function addNewOpinion(string memory _topic) public
    {
        opinionCount ++;

        opinions[opinionCount] = Opinion(opinionCount, _topic, 0, 0);
    }

}

contract Factory
{
    mapping (string => address) public categoryList;
    mapping (string => bool) public categoryExists;

    event opinionAdded(string _topic);
    event CategoryCreated(string _topicHeader);

    function addNewOpinion(string memory _topicHeader, string memory _topic) public
    {
        address categoryAddress = getCategoryAddress(_topicHeader);
        Category categoryInstance = Category(categoryAddress);

        require(categoryExists[_topicHeader], "New Category contract was not created");

        categoryInstance.addNewOpinion(_topic);

        emit opinionAdded(_topic);

    }

    function getCategoryAddress(string memory _topicHeader) public returns (address)
    {
        if(categoryExists[_topicHeader])
            return categoryList[_topicHeader];
        else
            createNewCategory(_topicHeader);
    }

    function createNewCategory(string memory _topicHeader) public returns (address)
    {
        Category t = new Category();
        t.setTopicHeader(_topicHeader);

        addCategory(_topicHeader, address(t));
        emit CategoryCreated(_topicHeader);
        return address(t);
    }

    function addCategory(string memory _topicHeader, address _address) public
    {
        categoryList[_topicHeader] = _address;
        categoryExists[_topicHeader] = true;
    }

}
